              document.cookie = "comply_cookie = comply_yes; expires = " + myDate.toGMTString(); //creates the cookie: name|value|expiry
              $("#cookies").slideUp("slow"); //jquery to slide it up
          });
      }
      
      // FIXME: va nel document ready dell'init?
      $('#custom-amount-select').click(function() {
          $("#optCustom").prop("checked", true);
      });
      
      // FIXME: va nell'init?
      function setTestessoValue(t_value){
          $("#testesso").val(t_value);
      
          $(".reservation-target div").removeClass("active");
          $("#sel_"+t_value).addClass("active");
          $("#testesso").trigger("change");
      }
   </script>
